import React, { useState, useEffect } from "react";
import {
  fetchSelfie,
  uploadSelfie,
  deleteSelfie,
  handleFaceRecognition,
} from "../services/selfieService";
import MatchedImages from "./MatchedImages";

const SelfieUpload = () => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [message, setMessage] = useState("");
  const [uploadedSelfie, setUploadedSelfie] = useState(
    localStorage.getItem("uploadedSelfie") || null
  );
  const [imageData, setImageData] = useState(
    localStorage.getItem("imageData") || null
  );
  const [matchedImages, setMatchedImages] = useState([]);

  useEffect(() => {
    if (uploadedSelfie && !imageData) {
      const loadImage = async () => {
        try {
          const imageUrl = await fetchSelfie(uploadedSelfie);
          setImageData(imageUrl);
          localStorage.setItem("imageData", imageUrl);
        } catch (error) {
          console.error(error);
          setMessage("Error fetching selfie");
        }
      };

      loadImage();
    }
  }, [uploadedSelfie, imageData]);

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setSelectedFile(file);
  };

  const handleUpload = async (e) => {
    e.preventDefault();
    if (!selectedFile) {
      setMessage("Please upload a selfie.");
      return;
    }

    try {
      const uploadMessage = await uploadSelfie(selectedFile);
      setMessage(uploadMessage);
      setUploadedSelfie(selectedFile.name);
      localStorage.setItem("uploadedSelfie", selectedFile.name);
    } catch (error) {
      console.error(error);
      setMessage("Error uploading selfie");
    }
  };

  const handleDelete = async () => {
    try {
      await deleteSelfie(uploadedSelfie);

      localStorage.removeItem("uploadedSelfie");
      localStorage.removeItem("imageData");

      setUploadedSelfie(null);
      setImageData(null);
      setMessage("Selfie deleted successfully");
    } catch (error) {
      console.error(error);
      setMessage("Error deleting selfie");
    }
  };

  const handleFaceRecognitionClick = async () => {
    if (!selectedFile) {
      setMessage("Please upload a selfie first.");
      return;
    }

    try {
      const matchedImages = await handleFaceRecognition(selectedFile);
      setMatchedImages(matchedImages);
      localStorage.setItem("matchedImages", JSON.stringify(matchedImages));
    } catch (error) {
      console.error(error);
      setMessage("Error during face recognition");
    }
  };

  return (
    <div style={{ paddingTop: "100px" }}>
      <h1>Upload Your Selfie</h1>
      <form onSubmit={handleUpload}>
        <input type="file" accept="image/*" onChange={handleFileChange} />
        <button type="submit">Upload</button>
      </form>
      {message && <p>{message}</p>}

      {imageData && (
        <div>
          <h2>Uploaded Selfie</h2>
          <img
            src={imageData}
            alt={`Uploaded selfie: ${uploadedSelfie}`}
            style={{ width: "150px", height: "150px", objectFit: "cover" }}
          />
        </div>
      )}

      {uploadedSelfie && (
        <div>
          <button onClick={handleFaceRecognitionClick}>
            Start Face Recognition
          </button>
          <button onClick={handleDelete} style={{ marginLeft: "10px" }}>
            Delete Selfie
          </button>
        </div>
      )}

      {matchedImages.length > 0 && (
        <MatchedImages
          matchedImages={matchedImages}
          uploadedSelfie={uploadedSelfie}
        />
      )}
    </div>
  );
};

export default SelfieUpload;
