import { useContext, useState } from 'react';
import axios from 'axios';
import {jwtDecode} from 'jwt-decode';
import { toast } from 'react-toastify';
import { UserContext } from './userContextApp';

const useUserApi = () => {
  const [isSignIn, setIsSignIn] = useState(false);
  const [errors, setErrors] = useState({});
  const [isLoginData, setIsLoginData] = useState({ email: '', password: '' });
  const [data, setData] = useState({
    name: { first: '', middle: '', last: '' },
    phone: '',
    email: '',
    password: '',
    image: { url: '', alt: '' },
    address: {
      state: '',
      country: '',
      city: '',
      street: '',
      houseNumber: '',
      zip: '',
    },
    isBusiness: false,
    isAdmin: false,
  });
  const { userInformation, setUserInformation } = useContext(UserContext);

  const handleSignInClick = () => {
    setIsSignIn(false);
  };

  const handleSignUpClick = () => {
    setIsSignIn(true);
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    const updatedValue = type === 'checkbox' ? checked : value;

    setData((prevData) => {
      const keys = name.split('.');
      if (keys.length > 1) {
        return {
          ...prevData,
          [keys[0]]: {
            ...prevData[keys[0]],
            [keys[1]]: updatedValue,
          },
        };
      } else {
        return {
          ...prevData,
          [name]: updatedValue,
        };
      }
    });

    setIsLoginData((prevLoginData) => ({
      ...prevLoginData,
      [name]: value,
    }));
  };

  const validate = () => {
    const newErrors = {};
    const validateField = (key, value) => {
      switch (key) {
        // Add your validation cases here
      }
    };

    const validateObject = (obj) => {
      Object.keys(obj).forEach((key) => {
        if (typeof obj[key] === 'object' && !Array.isArray(obj[key])) {
          validateObject(obj[key]);
        } else {
          validateField(key, obj[key]);
        }
      });
    };

    validateObject(data);
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (validate()) {
      const configRegister = {
        method: 'post',
        url: 'https://monkfish-app-z9uza.ondigitalocean.app/bcard2/users',
        data,
      };
      try {
        const response = await axios(configRegister);
        toast.success('Registration successful!');
        setData({
          name: { first: '', middle: '', last: '' },
          phone: '',
          email: '',
          password: '',
          image: { url: '', alt: '' },
          address: {
            state: '',
            country: '',
            city: '',
            street: '',
            houseNumber: '',
            zip: '',
          },
          isBusiness: false,
          isAdmin: false,
        });
        return response;
      } catch (error) {
        console.error(error);
        toast.error('Registration failed!');
      }
    } else {
      console.log('Validation failed');
    }
  };

  const handleSubmit2 = async (e) => {
    e.preventDefault();
    const configLoding = {
      method: 'post',
      url: 'https://monkfish-app-z9uza.ondigitalocean.app/bcard2/users/login',
      data: {
        email: isLoginData.email,
        password: isLoginData.password,
      },
    };
    try {
      const response = await axios(configLoding);
      const getToken = response.data;
      const decoded = jwtDecode(getToken);
      setUserInformation(decoded);
      localStorage.setItem('token', getToken);
      localStorage.setItem('userInfo', JSON.stringify(decoded));
      setIsLoginData({ email: '', password: '' });
      toast.success('Login successful!');
    } catch (error) {
      console.error(error);
      toast.error('Login failed!');
    }
  };

  return {
    isSignIn,
    errors,
    data,
    isLoginData,
    handleSignInClick,
    handleSignUpClick,
    handleChange,
    handleSubmit,
    handleSubmit2,
  };
};

export default useUserApi;
