import React, { useState, useEffect } from "react";
import axios from "axios";

const SelfieUpload = () => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [message, setMessage] = useState("");
  const [uploadedSelfie, setUploadedSelfie] = useState(null);
  const [recognizedImages, setRecognizedImages] = useState([]);

  // Handle file selection
  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setSelectedFile(file);
  };

  // Upload selfie to the server
  const handleUpload = async (e) => {
    e.preventDefault();
    if (!selectedFile) {
      setMessage("Please upload a selfie.");
      return;
    }

    const formData = new FormData();
    formData.append("image", selectedFile);

    try {
      const response = await axios.post(
        "http://localhost:5000/api/selfies/upload", // Upload the selfie to the server
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        }
      );
      setMessage(response.data.message);
      setUploadedSelfie(selectedFile.name); // Save the filename
    } catch (error) {
      console.error("Error uploading selfie:", error);
      setMessage("Error uploading selfie");
    }
  };

  // Trigger face recognition
  const handleFaceRecognition = async () => {
    if (!uploadedSelfie) {
      setMessage("Please upload a selfie first.");
      return;
    }

    try {
      const formData = new FormData();
      formData.append("sourceImage", selectedFile); // Send the selfie image for recognition

      const response = await axios.post(
        "http://localhost:5000/match-faces",
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        }
      );

      // Save recognized images in state and localStorage
      setRecognizedImages(response.data.matches);
      localStorage.setItem(
        "recognizedImages",
        JSON.stringify(response.data.matches)
      );
      setMessage("Face recognition completed.");
    } catch (error) {
      console.error("Error recognizing faces:", error);
      setMessage("Error recognizing faces.");
    }
  };

  // Retrieve recognized images from localStorage if they exist
  useEffect(() => {
    const savedImages = JSON.parse(localStorage.getItem("recognizedImages"));
    if (savedImages) {
      setRecognizedImages(savedImages);
    }
  }, []);

  return (
    <div style={{ paddingTop: "100px" }}>
      <h1>Upload Your Selfie</h1>
      <form onSubmit={handleUpload}>
        <input type="file" accept="image/*" onChange={handleFileChange} />
        <button type="submit">Upload</button>
      </form>
      {message && <p>{message}</p>}

      {/* Display face recognition button after selfie is uploaded */}
      {uploadedSelfie && (
        <div>
          <button onClick={handleFaceRecognition}>Run Face Recognition</button>
        </div>
      )}

      {/* Display recognized event images */}
      {recognizedImages.length > 0 && (
        <div>
          <h2>Recognized Event Images</h2>
          <div style={{ display: "flex", flexWrap: "wrap" }}>
            {recognizedImages.map((image, index) => (
              <div key={index} style={{ margin: "10px" }}>
                <img
                  src={`http://localhost:5000/images/${encodeURIComponent(
                    image.filename
                  )}`}
                  alt={`Recognized in: ${image.filename}`}
                  style={{
                    width: "150px",
                    height: "150px",
                    objectFit: "cover",
                  }}
                />
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default SelfieUpload;
