// frontend/src/components/SelfieUpload.js
import React, { useState, useEffect } from "react";
import axios from "axios";

const SelfieUpload = () => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [message, setMessage] = useState("");
  const [uploadedSelfies, setUploadedSelfies] = useState([]);

  // Fetch all uploaded selfies from the server
  useEffect(() => {
    const fetchSelfies = async () => {
      try {
        const response = await axios.get(
          "http://localhost:5000/all-images?type=selfie"
        );
        setUploadedSelfies(response.data);
      } catch (error) {
        console.error("Error fetching selfies:", error);
      }
    };

    fetchSelfies();
  }, []);

  const handleFileChange = (e) => {
    setSelectedFile(e.target.files[0]);
  };

  const handleUpload = async (e) => {
    e.preventDefault();
    if (!selectedFile) {
      setMessage("Please upload a selfie.");
      return;
    }

    const formData = new FormData();
    formData.append("image", selectedFile);

    try {
      const response = await axios.post(
        "http://localhost:5000/upload-selfie",
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        }
      );
      setMessage(response.data.message);

      // Fetch updated selfies list after upload
      const updatedSelfies = await axios.get(
        "http://localhost:5000/all-images?type=selfie"
      );
      setUploadedSelfies(updatedSelfies.data);
    } catch (error) {
      console.error("Error uploading selfie:", error);
      setMessage("Error uploading selfie.");
    }
  };

  return (
    <div style={{ paddingTop: "100px" }}>
      <h1>Upload Your Selfie</h1>
      <form onSubmit={handleUpload}>
        <input type="file" accept="image/*" onChange={handleFileChange} />
        <button type="submit">Upload Selfie</button>
      </form>
      {message && <p>{message}</p>}

      {/* Display uploaded selfies */}
      <h2>Uploaded Selfies</h2>
      <div style={{ display: "flex", flexWrap: "wrap" }}>
        {uploadedSelfies.map((selfie, index) => (
          <div key={index} style={{ margin: "10px" }}>
            <img
              src={`http://localhost:5000/images/${encodeURIComponent(
                selfie.filename
              )}`}
              alt={`Uploaded selfie: ${selfie.filename}`}
              style={{ width: "150px", height: "150px", objectFit: "cover" }}
            />
            <p>{selfie.filename}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default SelfieUpload;
