import React from "react";
import useCardsApi from "../../hooks/useCardsApi";
import styleFindTheOne from "./FoundTheOne.module.css";

const FindTheOne = () => {
  const [cards, loading, error] = useCardsApi();

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  if (!cards || cards.length === 0) {
    return <div>No cards found</div>;
  }

  const renderCardsWithFixedPosition = () => {
    const renderedCards = [];

    cards.forEach((card, index) => {
      renderedCards.push(
        <div key={card._id} className={styleFindTheOne.item}>
          <img
            className={styleFindTheOne.imgCards}
            src={card.image.url}
            alt={card.alt}
          />
          <h2>{card.title}</h2>
          <p className={styleFindTheOne.p}> {card.subtitle}</p>
          <p className={styleFindTheOne.p}>{card.address.country}</p>
        </div>
      );

      // Add a div with fixed position after every 6 cards
      if ((index + 1) % 6 === 0) {
        renderedCards.push(
          <div key={`fixed-div-${index}`} className={styleFindTheOne.fixedDiv}>
          helloe
          </div>
        );
      }
    });

    return renderedCards;
  };

  return (
    <div className="cardBody">
      <div className={styleFindTheOne.container_cards}>
        <div className={styleFindTheOne.frontCard}>
          <h1>Cards</h1>
          <ul className={styleFindTheOne.listOfCards}>
            {renderCardsWithFixedPosition()}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default FindTheOne;
