import { useState } from "react";

const useAddCards = () => {
  const [isClick, setIsClick] = useState(false);
  const [formValid,setFormValid] =useState(false);

  const addCard = () => {
    setIsClick(true);
  };
  const handleSubmit = (e) => {
    e.preventDefault();
    setFormValid(true)
  };
  return { isClick, addCard, handleSubmit ,formValid};
};

export default useAddCards;
