import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import SunIcon from "./images/Sunny.png";
import MoonIcon from "./images/Moon.png";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";

import SignInRegister from "./component/SignInRegister";
import MyCards from "./component/MyCards";
import FavoritCards from "./component/FavoritCards";
import Login from "./component/Login";
import "./App.css";
import "./Style/Navbar.css";

function App() {
  const [isSun, setIsSun] = useState(false);

  const handleIconClick = () => {
    setIsSun(!isSun);
  };
  useEffect(() => {
    if (isSun) {
      document.body.classList.add("dark-mode");
    } else {
      document.body.classList.remove("dark-mode");
    }
  }, [isSun]);
  return (
    <div>
      {/* <SignInRegister /> */}
      <nav className="container_nav">
        <ul className="link_list">
          <li>
            <Link className="link" to={"/about"}>
              About
            </Link>
          </li>
          <li>
            <Link className="link" to={"/myCards"}>
              My Cards
            </Link>
          </li>
          <li>
            <Link className="link" to={"/favCards"}>
              Favorit Cards
            </Link>
          </li>
          <li>
            <Link className="link" to={"/login"}>
              Login
            </Link>
          </li>
          <li>
            <Link className="link" to={"/register"}>
              register
            </Link>
          </li>
          <li>
            <input></input>
          </li>
          <li>
            <img
              style={{ width: "30px", height: "30px" }}
              src={isSun ? SunIcon : MoonIcon}
              alt="Sun Icon"
              onClick={handleIconClick}
            />
          </li>
        </ul>
      </nav>

      <Router>
        <Routes>
          <Route path="/about" />
          <Route path="/myCards" element={<MyCards />} />
          <Route path="/favCards" element={<FavoritCards />} />
          <Route path="/login" element={<Login />} />
          <Route path="/register" element={<SignInRegister />} />
        </Routes>
      </Router>
    </div>
  );
}

export default App;
