import React, { useState, useEffect } from "react";
import axios from "axios";

const SelfieUpload = () => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [message, setMessage] = useState("");
  const [matchingImages, setMatchingImages] = useState([]);

  // Load the selfie from localStorage when the component mounts
  useEffect(() => {
    const storedSelfie = localStorage.getItem("selfie");
    if (storedSelfie) {
      setSelectedFile(JSON.parse(storedSelfie));
    }
  }, []);

  // Save the selfie to localStorage whenever it changes
  useEffect(() => {
    if (selectedFile) {
      localStorage.setItem("selfie", JSON.stringify(selectedFile));
    }
  }, [selectedFile]);

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setSelectedFile(file);
  };

  const handleUpload = async (e) => {
    e.preventDefault();
    if (!selectedFile) {
      setMessage("Please upload a selfie.");
      return;
    }

    const formData = new FormData();
    formData.append("image", selectedFile);

    try {
      const response = await axios.post(
        "http://localhost:5000/upload-selfie",
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        }
      );

      setMessage(response.data.message);
      setMatchingImages(response.data.matchingImages || []);
    } catch (error) {
      console.error(error);
      setMessage("Error uploading selfie");
    }
  };

  return (
    <div style={{ paddingTop: "100px" }}>
      <h1>Upload Your Selfie</h1>
      <form onSubmit={handleUpload}>
        <input type="file" accept="image/*" onChange={handleFileChange} />
        <button type="submit">Upload</button>
      </form>
      {message && <p>{message}</p>}

      {/* Display the matching images */}
      <h2>Matching Images</h2>
      <div style={{ display: "flex", flexWrap: "wrap" }}>
        {matchingImages.length > 0 ? (
          matchingImages.map((image, index) => (
            <div key={index} style={{ margin: "10px" }}>
              <img
                src={`http://localhost:5000/images/${encodeURIComponent(
                  image.filename
                )}`}
                alt={image.filename ? `Uploaded image: ${image.filename}` : ""}
                style={{ width: "150px", height: "150px", objectFit: "cover" }}
              />
            </div>
          ))
        ) : (
          <p>No matching images found.</p>
        )}
      </div>
    </div>
  );
};

export default SelfieUpload;
