import React, { useState } from "react";

const ToggleButton = () => {
  const [isToggle, setIsToggle] = useState("On");

  function handleClick() {
    if (setIsToggle(isToggle)) {
      setIsToggle("Off");
    } else {
      setIsToggle("On");
    }
  }
  return (
    <div>
      <h1>Toggle Button</h1>
      <button onClick={handleClick}>{isToggle}</button>
    </div>
  );
};

export default ToggleButton;
