import React, { useState } from "react";

const ToggleButton = () => {
  const [isToggle, setIsToggle] = useState(true);

  function handleClick() {
    if (isToggle) {
      setIsToggle("Off");
    }
  }
  return (
    <div>
      <h1>Toggle Button</h1>
      <button onClick={handleClick}>{(isToggle = "ON")}</button>
    </div>
  );
};

export default ToggleButton;
