import React, { useState } from "react";
import {
  BrowserRouter as Router,
  Route,
  Routes,
  Navigate,
} from "react-router-dom";
import NavBar from "./component/Navbar";
import SignInRegister from "./component/SignInRegister";
import Footer from "./component/Footer";
import CustomConfirmDialog from "./component/CustomConfirmDialog"; // הוסף קומפוננטה כזו

import "./App.css";

function App() {
  const [isRegistered, setIsRegistered] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState();
  const [showConfirmation, setShowConfirmation] = useState(false); // סטייט לניהול האם להציג את האישור או לא

  const handleRegister = () => {
    setIsRegistered(true);
    setIsLoggedIn(true);
  };

  const handleLogout = () => {
    setShowConfirmation(true); // עם לחיצה על הלוגאוט, הצג את חלון האישור
  };

  const handleLogoutConfirmed = () => {
    setIsLoggedIn(false);
    setIsRegistered(false);
    setShowConfirmation(false); // לאחר אישור ההתנתקות, סגור את חלון האישור
  };

  const handleLogoutCancelled = () => {
    setShowConfirmation(false); // ביטול אישור ההתנתקות, סגור את חלון האישור
  };

  const handleSignIn = () => {
    setIsRegistered(false);
  };

  return (
    <div>
      {!isRegistered ? (
        <SignInRegister onRegister={handleRegister} />
      ) : (
        <Router>
          {isLoggedIn && <NavBar onLogout={handleLogout} />}
          <Routes>
            <Route path="/footer" element={<Footer />} />
            <Route path="/register" element={<SignInRegister />} />
            <Route path="/" element={<Navigate to="/register" />} />
          </Routes>
        </Router>
      )}
      {/* אם יש צורך להציג את חלון האישור */}
      {showConfirmation && (
        <CustomConfirmDialog
          message="Are you sure you want to log out?"
          onConfirm={handleLogoutConfirmed}
          onCancel={handleLogoutCancelled}
        />
      )}
    </div>
  );
}

export default App;
