import React from "react";
import useCardsApi from "../../hooks/useCardsApi";
import styleCards from "./Cards.module.css";
import useAddCards from "./useAddCards";

const Cards = () => {
  const [cards, loading, error] = useCardsApi();
  const { isClick, addCard, handleChange, handleSubmit, formValid } = useAddCards();

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  if (!cards || cards.length === 0) {
    return <div>No cards found</div>;
  }

  return (
  <div className="cardBody">
    <div className={styleCards.container_cards}>
      <div className={styleCards.frontCard}>
        <h1>Cards</h1>
        <ul className={styleCards.listOfCards}>
          {cards.slice(0, 3).map((card) => (
            <li className={styleCards.item} key={card._id}>
              <img
                className={styleCards.imgCards}
                src={card.image.url}
                alt={card.alt}
              />
              <h2>{card.title}</h2>
              <p className={styleCards.p}> {card.subtitle}</p>
              <p className={styleCards.p}>{card.address.country}</p>
            </li>
          ))}
        </ul>
        <button onClick={addCard}>+</button>
        {isClick && (
          <form className={styleCards.form} onSubmit={handleSubmit}>
            <div className={styleCards.sectionOne}>
              <label className={styleCards.label}>Title*:</label>
              <input
                className={styleCards.input}
                type="text"
                name="title"
                required
                onChange={handleChange}
              />
              <label className={styleCards.label}>Subtitle*:</label>
              <input
                className={styleCards.input}
                type="text"
                name="subtitle"
                required
                onChange={handleChange}
              />
            </div>
            <label>Description*:</label>
            <hr />
            <input
              className={styleCards.input}
              type="text"
              name="description"
              required
              onChange={handleChange}
            />

            <div className={styleCards.sectionTwo}>
              <label className={styleCards.label}>Phone*:</label>
              <input
                className={styleCards.input}
                type="tel"
                name="phone"
                required
                onChange={handleChange}
              />
              <label className={styleCards.label}>Email*:</label>
              <input
                className={styleCards.input}
                type="email"
                name="email"
                required
                onChange={handleChange}
              />
            </div>
            <label>Web (URL for the website):</label>
            <hr />
            <input
              className={styleCards.input}
              type="url"
              name="web"
              onChange={handleChange}
            />
            <div className={styleCards.sectionThree}>
              <label className={styleCards.label}>Image URL:</label>
              <input
                className={styleCards.input}
                type="url"
                name="imageUrl"
                onChange={handleChange}
              />
              <label className={styleCards.label}>Image Alt:</label>
              <input
                className={styleCards.input}
                type="text"
                name="imageAlt"
                onChange={handleChange}
              />
            </div>
            <hr />
            <label>City*:</label>
            <input
              className={styleCards.input}
              type="text"
              name="city"
              required
              onChange={handleChange}
            />
            <label>Country*:</label>
            <input
              className={styleCards.input}
              type="text"
              name="country"
              required
              onChange={handleChange}
            />
            <label>State:</label>
            <input
              className={styleCards.input}
              type="text"
              name="state"
              onChange={handleChange}
            />
            <hr />
            <label>Street*:</label>
            <input
              className={styleCards.input}
              type="text"
              name="street"
              required
              onChange={handleChange}
            />
            <label>House Number*:</label>
            <input
              className={styleCards.input}
              type="text"
              name="houseNumber"
              required
              onChange={handleChange}
            />
            <label>Zip*:</label>
            <input
              className={styleCards.input}
              type="text"
              name="zip"
              required
              onChange={handleChange}
            />
            <hr />
            <button type="submit" disabled={!formValid} style={{ cursor: formValid ? 'pointer' : 'not-allowed' }}>Send</button>
          </form>
        )}
      </div>
    </div>
  </div>
  );
};

export default Cards;
