import React, { useState } from "react";
import useCardsApi from "../../hooks/useCardsHooks/useCardsApi";
import styleFindTheOne from "./FoundTheOne.module.css";
import Loading from "../Loading/Loading";
import axios from "axios";
import FavoriteBorderIcon from "@mui/icons-material/FavoriteBorder";
import FavoriteIcon from "@mui/icons-material/Favorite";
import FavoritCards from "../FavoritCards/FavoritCards";

const FindTheOne = () => {
  const [cards, setCards, loading, error] = useCardsApi();
  const [likedCards, setLikedCards] = useState([]);

  const handleLike = async (cardId) => {
    try {
      const token = localStorage.getItem("token");
      const config = {
        method: "patch",
        url: `https://monkfish-app-z9uza.ondigitalocean.app/bcard2/cards/${cardId}`,
        headers: {
          "x-auth-token": token,
        },
      };
      await axios(config);

      // Check if cardId is already in likedCards
      if (likedCards.includes(cardId)) {
        // If it is, remove it from likedCards
        setLikedCards(likedCards.filter((id) => id !== cardId));
      } else {
        // If it's not, add it to likedCards
        setLikedCards([...likedCards, cardId]);
      }
    } catch (error) {
      console.error("Error liking/unliking card:", error);
    }
  };

  if (loading) {
    return <Loading />;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  if (!cards || cards.length === 0) {
    return <div>No cards found</div>;
  }

  return (
    <div className="bodyFindTheOne">
      <div className={styleFindTheOne.container_cards}>
        <div className={styleFindTheOne.frontCard}>
          <h1>Cards</h1>
          <ul className={styleFindTheOne.listOfCards}>
            {cards.map((card) => (
              <li className={styleFindTheOne.item} key={card._id}>
                <img
                  className={styleFindTheOne.imgCards}
                  src={card.image}
                  // alt={card.image.alt}
                />
                <h2>{card.title}</h2>
                <p className={styleFindTheOne.p}>{card.subtitle}</p>
                <p className={styleFindTheOne.p}>
                  {card.address.country}
                </p>
                <div onClick={() => handleLike(card._id)}>
                  {likedCards.includes(card._id) ? (
                    <FavoriteIcon style={{ color: "red" }} />
                  ) : (
                    <FavoriteBorderIcon />
                  )}
                </div>
              </li>
            ))}
          </ul>
        </div>
      </div>
      <FavoritCards likedCards={likedCards} />
    </div>
  );
};

export default FindTheOne;
